import styled from "styled-components";

import {GrMapLocation} from "react-icons/gr";
import {GiUnlitBomb} from "react-icons/gi";
import {BsSearch} from "react-icons/bs";

export const Container = styled.div`
  background: var(--color-header);
  border-bottom: 1px solid var(--color-border);

  display: flex;
  flex-direction: column;

  justify-content: space-between;

`;

export const SuperiorBar = styled.div`
  display: flex;
  justify-content: space-between;

`;

export const InferiorBar = styled.div`
  margin-top: 30px;
  display: flex;
`;

export const ShopIcon = styled.div`
  //margin: 40px ;
  >img{
    margin-left: 50px;
    width: 93px;
    height: 75px;
    vertical-align: middle;
  }
`;

export const SearchBar = styled.div`
  margin-top: auto;
  margin-bottom: auto;
  >input{
    background-color: white;
    height: 52px;
    width: 700px;
  }
`;

export const SearchIcon = styled(BsSearch)``;

export const PromotionCard = styled.div`
  display: flex;
`;

export const PromotionIcon = styled(GiUnlitBomb)`
  margin-top: auto;
  margin-bottom: auto;
  margin-right: 5px;
  height: 28px;
  width: 28px;
`;

export const PromotionMessage = styled.div`
  margin-right: 35px;
  margin-top: auto;
  margin-bottom: auto;
`;

export const CepCard = styled.div``;

export const CepIcon = styled(GrMapLocation)``;

export const CepMessage = styled.div``;

export const ColumnCard = styled.div`
  margin: auto;
  padding-left: 5px;
`;

export const InputWrapper = styled.div`
    width: 100%;

    padding-top: 15px;

    >input{

      padding-top: auto;
      padding-bottom: auto;
      margin-left: 20px;
      margin-right: 40px;

      width: 100%;
      height: 44px;

      padding: 0 10px 0 57px;
      border-radius: 7px;

      color: var(--white);
      background-color: white;


      //icone
      ~ svg{
          cursor: pointer;
          position: relative;
          top: -50%;
          left: 34px;
      }
    }

`;



export const Input = styled.div`
    width: 100%;
    height: 44px;

    padding: 0 10px 0 57px;
    border-radius: 7px;

    color: var(--white);
    background-color: var(--chat-input);

    position: relative;

    &::placeholder{
        color: var(--gray);
    }

    //icone
    ~ svg{
        position: relative;
        top: -50%;
        left: 14px;
        transition: 180ms ease-in-out;
    }
`;
